      <node TEXT="追求目标（理想目标）：镜片定位中央" ID="JFzptG0Upw" _mubu_text="%3Cspan%3E%E8%BF%BD%E6%B1%82%E7%9B%AE%E6%A0%87%EF%BC%88%E7%90%86%E6%83%B3%E7%9B%AE%E6%A0%87%EF%BC%89%EF%BC%9A%E9%95%9C%E7%89%87%E5%AE%9A%E4%BD%8D%E4%B8%AD%E5%A4%AE%3C/span%3E" STYLE="fork"/>
      <node TEXT="现实：并不是每个人都能达到理想目标，可能偏上一点，也可能偏下一点（只要偏移的量不是很大）" ID="d0p1nklzPX" _mubu_text="%3Cspan%3E%E7%8E%B0%E5%AE%9E%EF%BC%9A%E5%B9%B6%E4%B8%8D%E6%98%AF%E6%AF%8F%E4%B8%AA%E4%BA%BA%E9%83%BD%E8%83%BD%E8%BE%BE%E5%88%B0%E7%90%86%E6%83%B3%E7%9B%AE%E6%A0%87%EF%BC%8C%E5%8F%AF%E8%83%BD%E5%81%8F%E4%B8%8A%E4%B8%80%E7%82%B9%EF%BC%8C%E4%B9%9F%E5%8F%AF%E8%83%BD%E5%81%8F%E4%B8%8B%E4%B8%80%E7%82%B9%EF%BC%88%E5%8F%AA%E8%A6%81%E5%81%8F%E7%A7%BB%E7%9A%84%E9%87%8F%E4%B8%8D%E6%98%AF%E5%BE%88%E5%A4%A7%EF%BC%89%3C/span%3E" STYLE="fork"/>
