      <node TEXT="是对镜片静止不动时候的配适评估，评估的是镜片下泪液的间隙，通过泪液间隙推断镜片与角膜是否有接触，" ID="qlIqnYwThb" _mubu_text="%3Cspan%3E%E6%98%AF%E5%AF%B9%E9%95%9C%E7%89%87%E9%9D%99%E6%AD%A2%E4%B8%8D%E5%8A%A8%E6%97%B6%E5%80%99%E7%9A%84%E9%85%8D%E9%80%82%E8%AF%84%E4%BC%B0%EF%BC%8C%E8%AF%84%E4%BC%B0%E7%9A%84%E6%98%AF%E9%95%9C%E7%89%87%E4%B8%8B%E6%B3%AA%E6%B6%B2%E7%9A%84%E9%97%B4%E9%9A%99%EF%BC%8C%E9%80%9A%E8%BF%87%E6%B3%AA%E6%B6%B2%E9%97%B4%E9%9A%99%E6%8E%A8%E6%96%AD%E9%95%9C%E7%89%87%E4%B8%8E%E8%A7%92%E8%86%9C%E6%98%AF%E5%90%A6%E6%9C%89%E6%8E%A5%E8%A7%A6%EF%BC%8C%3C/span%3E" STYLE="fork">
        <node TEXT="但由于泪液是透明的，因此需通过荧光素钠染色，在钴蓝光的刺激下发出黄绿色的光，通过黄绿色光评估镜片下的泪液间隙从而推测镜片哪里松哪里紧" ID="I5WbJvi066" _mubu_text="%3Cspan%3E%E4%BD%86%E7%94%B1%E4%BA%8E%E6%B3%AA%E6%B6%B2%E6%98%AF%E9%80%8F%E6%98%8E%E7%9A%84%EF%BC%8C%E5%9B%A0%E6%AD%A4%E9%9C%80%E9%80%9A%E8%BF%87%E8%8D%A7%E5%85%89%E7%B4%A0%E9%92%A0%E6%9F%93%E8%89%B2%EF%BC%8C%E5%9C%A8%E9%92%B4%E8%93%9D%E5%85%89%E7%9A%84%E5%88%BA%E6%BF%80%E4%B8%8B%E5%8F%91%E5%87%BA%E9%BB%84%E7%BB%BF%E8%89%B2%E7%9A%84%E5%85%89%EF%BC%8C%E9%80%9A%E8%BF%87%E9%BB%84%E7%BB%BF%E8%89%B2%E5%85%89%E8%AF%84%E4%BC%B0%E9%95%9C%E7%89%87%E4%B8%8B%E7%9A%84%E6%B3%AA%E6%B6%B2%E9%97%B4%E9%9A%99%E4%BB%8E%E8%80%8C%E6%8E%A8%E6%B5%8B%E9%95%9C%E7%89%87%E5%93%AA%E9%87%8C%E6%9D%BE%E5%93%AA%E9%87%8C%E7%B4%A7%3C/span%3E" STYLE="fork"/>
        <node TEXT="通过增加黄色滤光片过滤钴蓝光，只留下荧光素钠发出的黄绿光，从而更容易评价泪液间隙" ID="YrvLpFgqUG" _mubu_text="%3Cspan%3E%E9%80%9A%E8%BF%87%E5%A2%9E%E5%8A%A0%E9%BB%84%E8%89%B2%E6%BB%A4%E5%85%89%E7%89%87%E8%BF%87%E6%BB%A4%E9%92%B4%E8%93%9D%E5%85%89%EF%BC%8C%E5%8F%AA%E7%95%99%E4%B8%8B%E8%8D%A7%E5%85%89%E7%B4%A0%E9%92%A0%E5%8F%91%E5%87%BA%E7%9A%84%E9%BB%84%E7%BB%BF%E5%85%89%EF%BC%8C%E4%BB%8E%E8%80%8C%E6%9B%B4%E5%AE%B9%E6%98%93%E8%AF%84%E4%BB%B7%E6%B3%AA%E6%B6%B2%E9%97%B4%E9%9A%99%3C/span%3E" STYLE="fork"/>
      </node>
      <node TEXT="荧光素钠" ID="n1iUsCixs3" _mubu_text="%3Cspan%3E%E8%8D%A7%E5%85%89%E7%B4%A0%E9%92%A0%3C/span%3E" STYLE="fork">
        <node TEXT="浓度的多少会影响配适评估：浓度越高，发出的光越亮" ID="cUa3nbtsZJ" _mubu_text="%3Cspan%3E%E6%B5%93%E5%BA%A6%E7%9A%84%E5%A4%9A%E5%B0%91%E4%BC%9A%E5%BD%B1%E5%93%8D%E9%85%8D%E9%80%82%E8%AF%84%E4%BC%B0%EF%BC%9A%E6%B5%93%E5%BA%A6%E8%B6%8A%E9%AB%98%EF%BC%8C%E5%8F%91%E5%87%BA%E7%9A%84%E5%85%89%E8%B6%8A%E4%BA%AE%3C/span%3E" STYLE="fork"/>
        <node TEXT="润湿" ID="SZynRPASpD" _mubu_text="%3Cspan%3E%E6%B6%A6%E6%B9%BF%3C/span%3E" STYLE="fork">
          <node TEXT="一般情况下使用生理盐水，有些会使用人工泪液（接近人体泪液效果更好,但贵），不建议用抗生素润湿（一般抗生素含防腐剂会刺激人眼导致反射性分泌眼泪会导致荧光素钠的浓度降低）" ID="jTG3lWZ1hV" _mubu_text="%3Cspan%3E%E4%B8%80%E8%88%AC%E6%83%85%E5%86%B5%E4%B8%8B%E4%BD%BF%E7%94%A8%E7%94%9F%E7%90%86%E7%9B%90%E6%B0%B4%EF%BC%8C%E6%9C%89%E4%BA%9B%E4%BC%9A%E4%BD%BF%E7%94%A8%E4%BA%BA%E5%B7%A5%E6%B3%AA%E6%B6%B2%EF%BC%88%E6%8E%A5%E8%BF%91%E4%BA%BA%E4%BD%93%E6%B3%AA%E6%B6%B2%E6%95%88%E6%9E%9C%E6%9B%B4%E5%A5%BD,%E4%BD%86%E8%B4%B5%EF%BC%89%EF%BC%8C%E4%B8%8D%E5%BB%BA%E8%AE%AE%E7%94%A8%E6%8A%97%E7%94%9F%E7%B4%A0%E6%B6%A6%E6%B9%BF%EF%BC%88%E4%B8%80%E8%88%AC%E6%8A%97%E7%94%9F%E7%B4%A0%E5%90%AB%E9%98%B2%E8%85%90%E5%89%82%E4%BC%9A%E5%88%BA%E6%BF%80%E4%BA%BA%E7%9C%BC%E5%AF%BC%E8%87%B4%E5%8F%8D%E5%B0%84%E6%80%A7%E5%88%86%E6%B3%8C%E7%9C%BC%E6%B3%AA%E4%BC%9A%E5%AF%BC%E8%87%B4%E8%8D%A7%E5%85%89%E7%B4%A0%E9%92%A0%E7%9A%84%E6%B5%93%E5%BA%A6%E9%99%8D%E4%BD%8E%EF%BC%89%3C/span%3E" STYLE="fork"/>
          <node TEXT="一般建议使用生理盐水或人工泪液，但临床中使用的是抗生素（预防眼部受到损伤）但是抗生素的滥用可能会导致细菌的耐药性" ID="AxHGsubFm7" _mubu_text="%3Cspan%3E%E4%B8%80%E8%88%AC%E5%BB%BA%E8%AE%AE%E4%BD%BF%E7%94%A8%E7%94%9F%E7%90%86%E7%9B%90%E6%B0%B4%E6%88%96%E4%BA%BA%E5%B7%A5%E6%B3%AA%E6%B6%B2%EF%BC%8C%E4%BD%86%E4%B8%B4%E5%BA%8A%E4%B8%AD%E4%BD%BF%E7%94%A8%E7%9A%84%E6%98%AF%E6%8A%97%E7%94%9F%E7%B4%A0%EF%BC%88%E9%A2%84%E9%98%B2%E7%9C%BC%E9%83%A8%E5%8F%97%E5%88%B0%E6%8D%9F%E4%BC%A4%EF%BC%89%E4%BD%86%E6%98%AF%E6%8A%97%E7%94%9F%E7%B4%A0%E7%9A%84%E6%BB%A5%E7%94%A8%E5%8F%AF%E8%83%BD%E4%BC%9A%E5%AF%BC%E8%87%B4%E7%BB%86%E8%8F%8C%E7%9A%84%E8%80%90%E8%8D%AF%E6%80%A7%3C/span%3E" STYLE="fork"/>
          <node TEXT="步骤" ID="k4bM4aIhc2" _mubu_text="%3Cspan%3E%E6%AD%A5%E9%AA%A4%3C/span%3E" STYLE="fork">
            <node TEXT="拆包装时扯白色一端，禁止扯黄色一端，避免荧光素钠被污染" ID="kACacLRpwj" _mubu_text="%3Cspan%3E%E6%8B%86%E5%8C%85%E8%A3%85%E6%97%B6%E6%89%AF%E7%99%BD%E8%89%B2%E4%B8%80%E7%AB%AF%EF%BC%8C%E7%A6%81%E6%AD%A2%E6%89%AF%E9%BB%84%E8%89%B2%E4%B8%80%E7%AB%AF%EF%BC%8C%E9%81%BF%E5%85%8D%E8%8D%A7%E5%85%89%E7%B4%A0%E9%92%A0%E8%A2%AB%E6%B1%A1%E6%9F%93%3C/span%3E" STYLE="fork"/>
            <node TEXT="手握白色一端，将黄色一端朝下后滴1~2滴生理盐水在黄色一端，且生理盐水悬空，切勿与黄色一端接触，只润湿黄色端的1/2或1/3" ID="1dglhUhDwo" _mubu_text="%3Cspan%3E%E6%89%8B%E6%8F%A1%E7%99%BD%E8%89%B2%E4%B8%80%E7%AB%AF%EF%BC%8C%E5%B0%86%E9%BB%84%E8%89%B2%E4%B8%80%E7%AB%AF%E6%9C%9D%E4%B8%8B%E5%90%8E%E6%BB%B41~2%E6%BB%B4%E7%94%9F%E7%90%86%E7%9B%90%E6%B0%B4%E5%9C%A8%E9%BB%84%E8%89%B2%E4%B8%80%E7%AB%AF%EF%BC%8C%E4%B8%94%E7%94%9F%E7%90%86%E7%9B%90%E6%B0%B4%E6%82%AC%E7%A9%BA%EF%BC%8C%E5%88%87%E5%8B%BF%E4%B8%8E%E9%BB%84%E8%89%B2%E4%B8%80%E7%AB%AF%E6%8E%A5%E8%A7%A6%EF%BC%8C%E5%8F%AA%E6%B6%A6%E6%B9%BF%E9%BB%84%E8%89%B2%E7%AB%AF%E7%9A%841/2%E6%88%961/3%3C/span%3E" STYLE="fork"/>
            <node TEXT="进行染色时，将黄色端朝上，白色段朝下，使生理盐水回流后对眼球进行染色，由于黄在上白在下因此很容易控制染色的量" ID="WCU2Zu31N0" _mubu_text="%3Cspan%3E%E8%BF%9B%E8%A1%8C%E6%9F%93%E8%89%B2%E6%97%B6%EF%BC%8C%E5%B0%86%E9%BB%84%E8%89%B2%E7%AB%AF%E6%9C%9D%E4%B8%8A%EF%BC%8C%E7%99%BD%E8%89%B2%E6%AE%B5%E6%9C%9D%E4%B8%8B%EF%BC%8C%E4%BD%BF%E7%94%9F%E7%90%86%E7%9B%90%E6%B0%B4%E5%9B%9E%E6%B5%81%E5%90%8E%E5%AF%B9%E7%9C%BC%E7%90%83%E8%BF%9B%E8%A1%8C%E6%9F%93%E8%89%B2%EF%BC%8C%E7%94%B1%E4%BA%8E%E9%BB%84%E5%9C%A8%E4%B8%8A%E7%99%BD%E5%9C%A8%E4%B8%8B%E5%9B%A0%E6%AD%A4%E5%BE%88%E5%AE%B9%E6%98%93%E6%8E%A7%E5%88%B6%E6%9F%93%E8%89%B2%E7%9A%84%E9%87%8F%3C/span%3E" STYLE="fork"/>
            <node TEXT="染色位置：眼球上方的球结膜（让患者向下看）" ID="e3S7ZnKxNc" _mubu_text="%3Cspan%3E%E6%9F%93%E8%89%B2%E4%BD%8D%E7%BD%AE%EF%BC%9A%E7%9C%BC%E7%90%83%E4%B8%8A%E6%96%B9%E7%9A%84%E7%90%83%E7%BB%93%E8%86%9C%EF%BC%88%E8%AE%A9%E6%82%A3%E8%80%85%E5%90%91%E4%B8%8B%E7%9C%8B%EF%BC%89%3C/span%3E" STYLE="fork"/>
            <node TEXT="染色的量是否合适：患者眼睛看下方，拉开上睑，染色后上睑不能马上放开，多拉开几秒，观察散开情况。染色后嘱患者闭眼睁开" ID="g4E1Q1glm9" _mubu_text="%3Cspan%3E%E6%9F%93%E8%89%B2%E7%9A%84%E9%87%8F%E6%98%AF%E5%90%A6%E5%90%88%E9%80%82%EF%BC%9A%E6%82%A3%E8%80%85%E7%9C%BC%E7%9D%9B%E7%9C%8B%E4%B8%8B%E6%96%B9%EF%BC%8C%E6%8B%89%E5%BC%80%E4%B8%8A%E7%9D%91%EF%BC%8C%E6%9F%93%E8%89%B2%E5%90%8E%E4%B8%8A%E7%9D%91%E4%B8%8D%E8%83%BD%E9%A9%AC%E4%B8%8A%E6%94%BE%E5%BC%80%EF%BC%8C%E5%A4%9A%E6%8B%89%E5%BC%80%E5%87%A0%E7%A7%92%EF%BC%8C%E8%A7%82%E5%AF%9F%E6%95%A3%E5%BC%80%E6%83%85%E5%86%B5%E3%80%82%E6%9F%93%E8%89%B2%E5%90%8E%E5%98%B1%E6%82%A3%E8%80%85%E9%97%AD%E7%9C%BC%E7%9D%81%E5%BC%80%3C/span%3E" STYLE="fork">
              <node TEXT="合适" ID="3n101RO8Cr" _mubu_text="%3Cspan%3E%E5%90%88%E9%80%82%3C/span%3E" STYLE="fork">
                <node TEXT="荧光素钠没有很明显的马上散开而是需要进行瞬目之后才散开" ID="sbSIrfLDxf" _mubu_text="%3Cspan%3E%E8%8D%A7%E5%85%89%E7%B4%A0%E9%92%A0%E6%B2%A1%E6%9C%89%E5%BE%88%E6%98%8E%E6%98%BE%E7%9A%84%E9%A9%AC%E4%B8%8A%E6%95%A3%E5%BC%80%E8%80%8C%E6%98%AF%E9%9C%80%E8%A6%81%E8%BF%9B%E8%A1%8C%E7%9E%AC%E7%9B%AE%E4%B9%8B%E5%90%8E%E6%89%8D%E6%95%A3%E5%BC%80%3C/span%3E" STYLE="fork"/>
                <node TEXT="荧光素钠在缓慢散开" ID="FmJHfiuaXT" _mubu_text="%3Cspan%3E%E8%8D%A7%E5%85%89%E7%B4%A0%E9%92%A0%E5%9C%A8%E7%BC%93%E6%85%A2%E6%95%A3%E5%BC%80%3C/span%3E" STYLE="fork"/>
              </node>
            </node>
          </node>
          <node TEXT="由于荧光素钠刚刚点上，浓度较高会影响观察配适，因此在点上以后的前半分钟（20~30s）的时间里可不用去看荧光素钠的亮度进行评估，但可以观察荧光素进入镜片下方时的方向、快慢从而在一定程度上推断配适的松紧度" ID="YKA9cLOEAL" _mubu_text="%3Cspan%3E%E7%94%B1%E4%BA%8E%E8%8D%A7%E5%85%89%E7%B4%A0%E9%92%A0%E5%88%9A%E5%88%9A%E7%82%B9%E4%B8%8A%EF%BC%8C%E6%B5%93%E5%BA%A6%E8%BE%83%E9%AB%98%E4%BC%9A%E5%BD%B1%E5%93%8D%E8%A7%82%E5%AF%9F%E9%85%8D%E9%80%82%EF%BC%8C%E5%9B%A0%E6%AD%A4%E5%9C%A8%3C/span%3E%3Cspan%20class=%22bold%22%3E%E7%82%B9%E4%B8%8A%E4%BB%A5%E5%90%8E%E7%9A%84%E5%89%8D%E5%8D%8A%E5%88%86%E9%92%9F%EF%BC%8820~30s%EF%BC%89%E7%9A%84%E6%97%B6%E9%97%B4%E9%87%8C%E5%8F%AF%E4%B8%8D%E7%94%A8%E5%8E%BB%E7%9C%8B%E8%8D%A7%E5%85%89%E7%B4%A0%E9%92%A0%E7%9A%84%E4%BA%AE%E5%BA%A6%E8%BF%9B%E8%A1%8C%E8%AF%84%E4%BC%B0%3C/span%3E%3Cspan%3E%EF%BC%8C%E4%BD%86%E5%8F%AF%E4%BB%A5%E8%A7%82%E5%AF%9F%E8%8D%A7%E5%85%89%E7%B4%A0%E8%BF%9B%E5%85%A5%E9%95%9C%E7%89%87%E4%B8%8B%E6%96%B9%E6%97%B6%E7%9A%84%E6%96%B9%E5%90%91%E3%80%81%E5%BF%AB%E6%85%A2%E4%BB%8E%E8%80%8C%E5%9C%A8%E4%B8%80%E5%AE%9A%E7%A8%8B%E5%BA%A6%E4%B8%8A%E6%8E%A8%E6%96%AD%E9%85%8D%E9%80%82%E7%9A%84%E6%9D%BE%E7%B4%A7%E5%BA%A6%3C/span%3E" STYLE="fork"/>
        </node>
        <node TEXT="对荧光素钠分布的评估——评估亮度（亮度越亮，浓度越高），由于此时浓度基本达到稳定状态不会影响亮度因此可以通过亮度反应泪液间隙的大小" ID="wAo9InWnr7" _mubu_text="%3Cspan%3E%E5%AF%B9%E8%8D%A7%E5%85%89%E7%B4%A0%E9%92%A0%E5%88%86%E5%B8%83%E7%9A%84%E8%AF%84%E4%BC%B0%E2%80%94%E2%80%94%E8%AF%84%E4%BC%B0%E4%BA%AE%E5%BA%A6%EF%BC%88%E4%BA%AE%E5%BA%A6%E8%B6%8A%E4%BA%AE%EF%BC%8C%E6%B5%93%E5%BA%A6%E8%B6%8A%E9%AB%98%EF%BC%89%EF%BC%8C%E7%94%B1%E4%BA%8E%E6%AD%A4%E6%97%B6%E6%B5%93%E5%BA%A6%E5%9F%BA%E6%9C%AC%E8%BE%BE%E5%88%B0%E7%A8%B3%E5%AE%9A%E7%8A%B6%E6%80%81%E4%B8%8D%E4%BC%9A%E5%BD%B1%E5%93%8D%E4%BA%AE%E5%BA%A6%E5%9B%A0%E6%AD%A4%E5%8F%AF%E4%BB%A5%E9%80%9A%E8%BF%87%E4%BA%AE%E5%BA%A6%E5%8F%8D%E5%BA%94%E6%B3%AA%E6%B6%B2%E9%97%B4%E9%9A%99%E7%9A%84%E5%A4%A7%E5%B0%8F%3C/span%3E" STYLE="fork">
